import React from "react";

const FormInput = ({handleClick, label, ...otherProps}) => (
  <div className="group">
    <input
      onclick={handleClick}
      {...otherProps}
    />
    {label ? <label className={otherProps.value.length ? `shrink` : ``}>{label}</label> : null}
  </div>
)

export default FormInput;
