import {createGlobalStyles} from 'styled-components';

export const GlobalStyles = createGlobalStyles`
  body {
    font-family: 'Open Sans Condensed';
    padding: 20px 30px;
  }

  a {
    text-decoration: none;
    color: black;
  }

  * {
    box-sizing: border-box;
  }
`;
